import { useEffect, useState } from 'react';
import API from '../api';

const Home = () => {
  const [posts, setPosts] = useState([]);
  const [content, setContent] = useState('');

  const fetchPosts = async () => {
    try {
      const res = await API.get('/posts');
      setPosts(res.data);
    } catch (err) {
      alert('Failed to fetch posts');
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  const submitPost = async (e) => {
    e.preventDefault();
    try {
      await API.post('/posts', { content });
      setContent('');
      fetchPosts();
    } catch (err) {
      alert('Failed to create post');
    }
  };

  return (
    <div className="max-w-xl mx-auto p-4">
      <form onSubmit={submitPost} className="space-y-2">
        <textarea
          className="w-full p-2 border rounded"
          placeholder="What's on your mind?"
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
        <button className="bg-green-500 text-white px-4 py-2 rounded">Post</button>
      </form>
      <div className="mt-6">
        {posts.map((post) => (
          <div key={post.id} className="border p-3 my-2 rounded shadow">
            <p className="font-semibold">{post.author.name}</p>
            <p className="text-gray-600 text-sm">{new Date(post.createdAt).toLocaleString()}</p>
            <p>{post.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
